import tkinter as tk
import re


def format_css(css):
    semicolon = re.compile(r';')
    braces = re.compile(r'{')
    extra= re.compile(r'\n{2,}')


    # Add newline after each closing brace
    formatted = re.sub(r'}', r"}\n", css)

    # Indent each line
    # indent_level = 0
    # formatted = re.sub(r'[{}]', lambda match: '\n' + '\t' * indent_level + match.group(0) if match.group(0) == '{' else '\n' + '\t' * (indent_level-1) + '}', formatted)

    # Remove extra newlines
    formatted = extra.sub( r'\n', formatted)

    # Remove whitespace before commas
    # formatted = re.sub(r'\s*,\s*', ', ', formatted)
    formatted = semicolon.sub( r';\n\t', formatted)
    formatted = braces.sub( r'{\n  ', formatted)

    return formatted


root = tk.Tk()

input_text = tk.Text(root)
input_text.pack(side="left", expand=True, fill="both")

output_text = tk.Text(root)
output_text.pack(side="left", expand=True, fill="both")

def update_output(evet):
  input_css = input_text.get("1.0", "end")
  output_css = format_css(input_css)
  output_text.delete("1.0", "end")
  output_text.insert("1.0", output_css)

input_text.bind("<KeyRelease>", update_output)

root.mainloop()